mapper.select:

SELECT COUNT(*) FROM TBL_COMMENT
WHERE
	MUSIC_CLASS_NO = #{musicClassNo}

SELECT M.MEMBER_USERID as memberUserId , C.COMMENT_CONTENTS as commentContents , C.REG_DATETIME as regDatetime , C.MUSIC_CLASS_NO as musicClassNo , F.WEB_PATH as webPath , F.FILE_PATH as filePath , F.FILE_NAME as fileName , F.FILE_NAME2 as fileName2 FROM TBL_COMMENT AS C LEFT OUTER JOIN TBL_MEMBER AS M ON ( C.MEMBER_NO=M.MEMBER_NO ) LEFT OUTER JOIN TBL_ATTACH_MUSIC AS F ON( C.MUSIC_NO = F.MUSIC_NO ) WHERE 1 = 1
AND C.MUSIC_CLASS_NO = #{musicClassNo}
LIMIT #{pageCount}, #{pageOffset}
